mapper.select:

	select 	*,		  
		
	from job_purchase
	where 
		deleted = 'N'
		 
		and id = #{jobPurchaseId}		
		
		
		and job_order_id = #{jobOrderid}
		
		
		and partner_id = #{partnerId}


	select 	*,		  
		
	from job_purchase
	where 
		deleted = 'N'
		 
		and id = #{jobPurchaseId}		
		
		
		and job_order_id = #{jobOrderId}
		
		
		and partner_id = #{partnerId}

select d.job_order_id, d.partner_id, d.reg_date, (select device from job_order where id = d.job_order_id) as device, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where id = d.job_order_id) as job_order_no, (select partner_name from job_partner where id = d.partner_id) as partnerName, count(*) as count from job_purchase d inner join job_order a on a.id = d.job_order_id where d.deleted = 'N' and d.stage in ('R','E')
and a.order_no_base = #{orderNoBase}
and a.order_no_extra = #{orderNoExtra}
and d.round_robinYN = #{roundRobinYN}
group by job_order_id, partner_id order by partner_id, job_order_id

select E.*, count(*) as count, E.estimate_request_id, FN_GetDisplayOrderNo(job.order_type, job.order_no_base, job.order_no_extra) as job_order_no, FN_GetPartnerName(P.partner_id) as partnerNameD, FN_GetPartnerName(E.partner_id) as partnerName, job.device, sum(E.estimated_price * E.quantity) as sum from purchase_estimate E inner join job_order job on (E.job_order_id=job.id) inner join job_purchase P on (E.job_purchase_id=P.id)
job.device like concat('%', #{desc}, '%')
and P.stage in('R','E')
and E.maker = #{maker}
and E.partner_id = #{partnerId}
and job.order_no_base = #{orderNoBase}
and job.order_no_extra = #{orderNoExtra}
and E.reg_date between #{convertedDesignDateBegin} and date_add(#{convertedDesignDateEnd}, interval 1 day)
and E.reg_date &gt; #{convertedDesignDateBegin}
and E.reg_date < date_add(#{convertedDesignDateEnd}, interval 1 day)
group by E.estimate_request_id order by E.reg_date DESC;

select d.job_order_id, d.partner_id, d.reg_date, (select device from job_order where id = d.job_order_id) as device, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where id = d.job_order_id) as job_order_no, (select partner_name from job_partner where id = d.partner_id) as partnerName, count(*) as count from job_purchase d inner join job_order a on a.id = d.job_order_id where d.deleted = 'N' and d.stage in ( 'R', 'E')
and a.order_no_base like concat(#{orderNoBase}, '%')
and a.order_no_extra = #{orderNoExtra}
and d.round_robinYN = #{roundRobinYN}
group by job_order_id, partner_id order by partner_id, job_order_id

select I.*, count(*) as count, I.issue_request_id, FN_GetDisplayOrderNo(job.order_type, job.order_no_base, job.order_no_extra) as job_order_no, FN_GetPartnerName(P.partner_id) as partnerNameD, FN_GetPartnerName(I.partner_id) as partnerName, job.device, sum(I.issue_price * I.quantity) as sum from purchase_issue I inner join job_order job on (I.job_order_id=job.id) inner join job_purchase P on (I.job_purchase_id=P.id) where I.cancle ='N'
and job.device like concat('%', #{desc}, '%')
and I.issue_type in('C','H')
and I.partner_id = #{partnerId}
and job.order_no_base = #{orderNoBase}
and job.order_no_extra = #{orderNoExtra}
and I.reg_date between #{convertedDesignDateBegin} and date_add(#{convertedDesignDateEnd}, interval 1 day)
and I.reg_date &gt; #{convertedDesignDateBegin}
and I.reg_date < date_add(#{convertedDesignDateEnd}, interval 1 day)
group by I.issue_request_id order by I.reg_date DESC;

mapper.update:
update job_purchase
set
stage = 'E', estimate_request_id = #{estimate_request_id}, estimate_requested_when = now(), estimate_request_user_id = #{estimate_request_user_id}
where id = #{id};

		update 
			purchase_estimate
		
		set 
	
			receive_date = #{receiveDate},
			estimated_price = #{estimatedPrice}
		
		where job_purchase_id = #{jobPurchaseId} and estimate_request_id = #{estimateRequestId} and job_order_id = #{jobOrderId};

mapper.select:
select *, job_order_id, partner_id, (select device from job_order where job_order.id = job_purchase.job_order_id) as device, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = job_purchase.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = job_purchase.partner_id) as partnerName from job_purchase
job_order_id in
#{V}
and partner_id = #{partnerId}
and deleted = 'N' and stage = 'R'
order by job_order_id

select
(select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = d.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = d.partner_id) as partnerName, (select count(*) from job_purchase where partner_id=h.partner_id and stage='P' and order_request_id = h.issue_request_id) as noneinnersotck, (select sum(issue_price*quantity) from purchase_issue where job_purchase_id in((select id from job_purchase where stage='P' and order_request_id = h.issue_request_id))) as sumPrice, count(*) as count, h.* from purchase_issue h inner join job_purchase d on d.deleted ='N' and d.stage= 'P' and d.id = h.job_purchase_id where h.cancle = 'N'
and d.partner_id = #{partnerId}
and h.reg_date between #{convertedDesignDateBegin} and date_add(#{convertedDesignDateEnd}, interval 1 day)
and h.reg_date &gt; #{convertedDesignDateBegin}
and h.reg_date < date_add(#{convertedDesignDateEnd}, interval 1 day)
and d.round_robinYN = #{roundRobinYN}
group by issue_request_id, job_order_id order by h.receive_date

select (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = d.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = d.partner_id) as partnerName, (select issue_price from job_purchase_history where d.id=h.job_purchase_id and d.stage = 'P' and h.request_type='R') as issue_pricell, (select MAX(issue_price) from job_purchase_history where model_no=d.model_no and d.stage = 'P' and h.request_type='R') as MaxPrice, (select MIN(issue_price) from job_purchase_history where model_no=d.model_no and d.stage = 'P' and h.request_type='R') as MinPrice, (select issue_date from job_purchase_history where d.id=h.job_purchase_id and d.stage = 'P' and request_type='R') as OrderIssue_date, (select device from job_order where job_order.id = d.job_order_id) as device, (select quantity from stock where model_no=h.model_no and deleted = 'N') as stockQuantity ,d.unit_no, d.description, d.seq, d.spare,d.quantity,d.job_order_id, d.partner_id, h.* from job_purchase_history h inner join job_purchase d on d.id = h.job_purchase_id
job_order_id in
#{V}
and partner_id = #{partnerId}
order by job_order_id

select (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = d.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = d.partner_id) as partnerName, (select device from job_order where job_order.id = d.job_order_id) as device, (select quantity from stock where model_no=d.model_no and deleted = 'N') as stockQuantity, (select ab_quantity from stock where model_no=d.model_no and deleted = 'N') as abQuantity ,d.id,d.unit_no, d.description, d.model_no, d.remark, d.comment, d.seq, d.spare,d.quantity, d.job_order_id, d.partner_id, d.maker, d.stock_use_quantity from job_purchase d
d.stage in('R','E') and
job_order_id in
#{V}
and partner_id = #{partnerId}
order by job_order_id

select (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = d.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = d.partner_id) as partnerName, (select device from job_order where job_order.id = d.job_order_id) as device, IFNULL((select (quantity-(IFNULL(ab_quantity,0)+IFNULL(out_quantity,0))) from stock where model_no=d.model_no and deleted = 'N'),0) as stockqty, (select quantity+spare from job_purchase where id=d.id), (select MIN(estimated_price) from purchase_estimate where job_order_id=d.id and job_purchase_id=d.id) as minestimatedPrice, (select MIN(issue_price) from purchase_issue where model_no=d.model_no) as min, (select MAX(issue_price) from purchase_issue where model_no=d.model_no) as max, IFNULL(d.spare,0) as spare, d.id,d.unit_no, d.description, d.model_no, d.remark , d.seq, d.quantity, d.job_order_id, d.partner_id, d.maker, d.stock_use_quantity, d.robin_request_date, d.round_robinYN from job_purchase d
d.stage in('R','E') and
job_order_id in
#{V}
and partner_id = #{partnerId}
order by job_order_id

select (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = d.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = d.partner_id) as partnerName, (select device from job_order where job_order.id = d.job_order_id) as device, d.code ,d.unit_no, d.description, d.seq, d.comment, d.maker, h.* from purchase_issue_before h inner join job_purchase d on d.id = h.job_purchase_id
where (d.quantity-h.ab_quantity) > 0
and h.job_order_id in
#{V}
and h.partner_id = #{partnerId}
order by h.job_order_id

select h.*, (select device from job_order where job_order.id = h.job_order_id) as device, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = h.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = h.partner_id) as partnerName, d.unit_no, d.seq, d.code, d.remark from purchase_estimate_before h inner join job_purchase d on d.id = h.job_purchase_id
h.job_order_id in
#{V}
and h.partner_id = #{partnerId}
order by h.job_order_id

select *, (select device from job_order where job_order.id = job_purchase.job_order_id) as device, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = job_purchase.job_order_id) as job_order_no, (select partner_name from job_partner where job_partner.id = job_purchase.partner_id) as partnerName from job_purchase where deleted = 'N'
and partner_id = #{partnerId}
and maker = #{maker}
and description like concat('%', #{desc}, '%')
and model_no like concat('%', #{modelNo}, '%')
and unit_no = #{unitNo}
order by reg_date DESC


