mapper.select:
	select d.*
      from stock d

		where d.id=#{id}

select * 

from stock_out_histroy 

where id = #{id}

select d.*, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = h.job_order_id) as orderNo, (select partner_name from job_partner where id=d.partner_id) as partnerName, (select count(id) from statement_of_account_detail where issue_id = d.id) as count, h.order_requested_when, h.warehousing_date from job_purchase h inner join statement_of_account d on h.order_request_id = d.request_id and h.job_order_id = d.job_order_id and d.partner_type_kind = 'P' and d.issue_date = '0001-01-01'
and d.partner_id = #{PartnerCode}
and h.order_requested_when between #{convertedDesignDateBegin} and date_add(#{convertedDesignDateEnd}, interval 1 day)
and h.order_requested_when &gt; #{convertedDesignDateBegin}
and h.order_requested_when < date_add(#{convertedDesignDateEnd}, interval 1 day)
group by d.id, h.job_order_id

select (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = h.job_order_id) as job_order_no, (select partner_name from job_partner where id=h.partner_id) as partnerName, (select count(id) from statement_of_account_detail where issue_id = h.id) as count, h.request_id, h.id, h.sum_price, h.issue_date
from statement_of_account h inner join statement_of_account_detail d on h.id = d.issue_id and h.partner_type_kind = 'P' and h.issue_date != '0001-01-01'
and h.partner_id = #{PartnerCode}
and (select order_no_base from job_order where job_order.id = (select job_order_id from job_purchase where id=d.job_purchase_id)) = #{orderNoBase}
and (select order_no_extra from job_order where job_order.id = (select job_order_id from job_purchase where id=d.job_purchase_id)) = #{orderNoExtra}
and h.issue_date between #{convertedDesignDateBegin} and date_add(#{convertedDesignDateEnd}, interval 1 day)
and h.issue_date &gt; #{convertedDesignDateBegin}
and h.issue_date < date_add(#{convertedDesignDateEnd}, interval 1 day)
group by h.id

	select job_purchase_id
      from stock 

		where id=#{id}

select d.job_order_id, count(d.job_order_id) as countOrderNo, (select FN_GetDisplayOrderNo(order_type, order_no_base, order_no_extra) from job_order where job_order.id = d.job_order_id) as job_order_no, (select current_stage from job_order where id = d.job_order_id) as currnet from statement_of_account as d
where d.partner_type_kind = 'P' and d.issue_date != '0001-01-01'
and (select order_no_base from job_order where id = d.job_order_id) = #{orderNoBase}
and (select order_no_extra from job_order where id = d.job_order_id) = #{orderNoExtra}
and d.issue_date between #{convertedDesignDateBegin} and date_add(#{convertedDesignDateEnd}, interval 1 day)
and d.issue_date &gt; #{convertedDesignDateBegin}
and d.issue_date < date_add(#{convertedDesignDateEnd}, interval 1 day)
group by d.job_order_id
order by d.job_order_id
